// select checkboxes to style: $(':checkbox').styleCheckboxes();
$.fn.styleCheckboxes = function() {
    $(this).each(function(index) {
        if (!$(this).prev("a").hasClass('check-styled')) {
            $(this).before("<a href='#check_" + index + "' class='check-styled' id='check_" + index + "'></a>").css({
                position: 'absolute',
                left: -1,
                top: 3,
                zIndex: -1,
                visibility: "hidden"
            });
            if ($(this).prop('checked'))
                $(this).prev().addClass('checked');
            $(this).change(function() {
                if ($(this).prop('checked')) {
                    $(this).prev().addClass('checked');
                } else {
                    $(this).prev().removeClass('checked');
                }
            });
        }
    });
    // function to 'check' the styled ones and their matching checkboxes
    $(".check-styled").on('click', function() {
        $(this).next("input[type=checkbox]").trigger("click").trigger('change');
        return false;
    });
};

// select radios to style: $(':radio').styleCheckboxes();
$.fn.styleRadioButtons = function() {
    $(this).each(function(index) {
        if (!$(this).prev("a").hasClass('radio-styled')) {
            var group = $(this).attr('name');
            $(this).before('<a href="#radio_' + index + '" class="radio-styled" id="radio_' + index +
                    '" rel="rg-' + group + '"></a>')
            .css({
                position: 'absolute',
                left: -1,
                top: 3,
                zIndex: -1,
                visibility: "hidden"
            });
            $(this).parent().find("input[type=radio]").each(function() {
                (this.checked) ? $(this).prev().addClass('checked') : $(this).prev().removeClass('checked');
            });
            $(this).change(function() {
                if ($(this).prop('checked')) {
                    var group = 'rg-' + $(this).attr('name');
                    var excId = $(this).prev().attr('id');
                    $(this).parent().find('.radio-styled[rel=' + group + ']').not('#' + excId).removeClass('checked');
                    $(this).prev().addClass('checked');
                } else {
                    $(this).prev().removeClass('checked');
                }
            });
        }
    });
    // function to 'check' the styled ones and their matching checkboxes
    $(".radio-styled").on('click', function() {
        var group = $(this).attr('rel');
        $(this).parent().find('.radio-styled[rel=' + group + ']').removeClass('checked');
        $(this).addClass('checked');
        $(this).next("input[type=radio]")[0].click();          
        
        return false;
    });
};


// Returns a function, that, as long as it continues to be invoked, will not
// be triggered. The function will be called after it stops being called for
// N milliseconds. If `immediate` is passed, trigger the function on the
// leading edge, instead of the trailing.
$.fn.debounce = function(func, wait, immediate) {
	var timeout;
	return function() {
		var context = this, args = arguments;
		var later = function() {
			timeout = null;
			if (!immediate) func.apply(context, args);
		};
		var callNow = immediate && !timeout;
		clearTimeout(timeout);
		timeout = setTimeout(later, wait);
		if (callNow) func.apply(context, args);
	};
};

